<template>
  <div>
    <app-header></app-header>
    <h1>{{ title }}</h1>
    <app-member v-bind:members="teamMembers"></app-member> <!-- here is where we pass the members back to its parent component -->
    <app-footer></app-footer>
  </div>
</template>

<script>

// this is how you nest a component locally
import Header from './components/Header.vue'
import Footer from './components/Footer.vue'
import Members from './components/Members.vue'

export default {
  // here is where you use that define the component so that you can use it
  components: {
    // components essentially allows you to create your own custom html tags
    // and you use your custom tags like a normal html tag (<ninjas></ninjas> is used like
    // <h1></h1>)
    // however, you can't make custom tags that are html tags;
    // that is why we call this custom tag 'app-header' and not 'header' since
    // 'header' is a default html tag
    'app-header': Header,
    // same goes for using 'app-footer' and not 'footer'
    'app-footer': Footer,
    // you don't have to call it 'app-member'; it's just to keep it consistent with the other components and it helps 
    // to differentiate other properties that have similar variable names
    'app-member': Members,

  },

  data() {
    return {
        title: "Board Members",
        // below here is where we defined the members; you could've done it in the child component (Members), but
        // this is simply to illustrate the idea of a prop
        // in this example, App is the parent component of Members, and App will give this prop (array of members) to
        // its child component, Members
        teamMembers: [
          // the show property is used to toggle when the position property will be shown 
            {name: 'Brian Nguyen', position: 'Chief of Backend Development', description: 'A cool ninja',
             picture: 'data:image/png;base64,',show: false},
            {name: 'Ayush Khanal', position: 'CEO', description: 'A strong brown man',
             picture: 'data:image/jpeg;base64,', show: false},
            {name: 'Matthew Strong', position: 'CTO', description: 'An actual GOD',
             picture: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTnpBeL5cQsqdAMf3rF14xFmJh8cChilmA2hrB_r9iKNCMzSTmU9Q', show: false},
            {name: 'Satish Upadhyaya', position: 'Chief Operating Officer', description: 'A smart dude',
             picture: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTzKVx7WvIsF8vOEG95b1zl9wckgSNBT72cZBmExOHUroY79Vklqw', show: false},
            {name: 'Person 1', position: 'Future Board Member', description: 'Some rando',
             picture: 'https://thumbs.dreamstime.com/b/cartoon-person-12066711.jpg', show: false},
            {name: 'Person 2', position: 'Future Board Member', description: 'Another random person',
             picture: 'https://thumbs.dreamstime.com/b/cartoon-person-12066711.jpg', show: false},
            {name: 'Person 3', position: 'Future Board Member', description: 'Yet another random person',
             picture: 'https://thumbs.dreamstime.com/b/cartoon-person-12066711.jpg', show: false},
        ]
    }
  },

}

</script>

<!-- this is without scope 
<style>
// here is how you can set a "default" style for all of the components
// nested with this component
// if you want to change the style of a component nested to this component, then
// you can go to that nested component and override the style there 
  h1 {
      color: purple;
  }
</style>
// if you don't scope, then the nested component could completely override the style
-->

<!-- by using scoped here, you can make the style component specific
you also have to add scoped to the nested component too -->
<style scoped>
  h1 {
      color: black;
  }
</style>